const initialState = {
    counterRedux : 0  ,
    results: []
}



const reducer = (state = initialState , action) => {
    switch (action.type) {
        case 'INCREMENT': 
        case 'SUBSTRACT':
        case 'DECREMENT':
        case 'ADD':              
            return {
                ...state , 
                counterRedux: state.counterRedux + action.payLoad.value
            }
        case 'STORE_RESULT':
            const newResults = [...state.results , state.counterRedux];            
            return {
                ...state,
                counterRedux: newResults
            }    
        case 'DELETE_RESULT':            
            return {
                ...state,
                counterRedux: newResults
            }
        default:
            break;
    }    
    return state;
}


export default reducer;
