const initialState = {
    counter : 0 
}



const reducer = (state = initialState , action) => {
    switch (action.type) {
        case 'INCREMENT':
            const incrementedState = {
                ...state,
                counter : state.counter + 1
            }
            return incrementedState;
        default:
            break;
    }    
    return state;
}


export default reducer;
