const initialState = {
    counterRedux : 0 
}



const reducer = (state = initialState , action) => {
    switch (action.type) {
        case 'INCREMENT': 
        case 'SUBSTRACT':
        case 'DECREMENT':
            return {
                ...state,
                counterRedux : state.counterRedux + action.payLoad.value
            };
        case 'ADD':
            return {
                ...state , 
                counterRedux: state.counterRedux + action.payLoad.value
            }
        default:
            break;
    }    
    return state;
}


export default reducer;
